
public class DifferentWays {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//different ways to write "3+7"
		System.out.println("3 + 7"); //option1
		
		System.out.println("3" + "7"); //option2
		
		System.out.println("3"); //option3
		System.out.println("+ 7");
		
		System.out.print("3 "); //option4
		System.out.println("+ 7");
		
		System.out.println(3 + 7); 

	//parentheses work as usuall
	System.out.println(2+3*4);
	System.out.println((2+3)*4);
	//floating point division
	System.out.println(25/10.0);
	System.out.println(25.0/10);
	//integer division and ramainder
	System.out.println(25/10);
	System.out.println(25%10);
	//true/false values
	System.out.println(2+3<6);
	System.out.println(2+3>6);
	System.out.println(2+3<5);
	System.out.println(2+3>5);
	System.out.println(2+3<=5);
	System.out.println(2+3!=5);
	
	
	
	}

}
